SELECT queriesReceived.networkQueryId, executionsCompleted.replyId, resultsSent.replyId, shrineNodeId, userName, queriesReceived.queryName, topicId, topicName, FROM_UNIXTIME(timeQuerySent/1000), FROM_UNIXTIME(timeReceived/1000), FROM_UNIXTIME(timeExecutionStarted/1000), FROM_UNIXTIME(timeExecutionCompleted/1000), FROM_UNIXTIME(timeResultsSent/1000) FROM queriesReceived LEFT OUTER JOIN executionsStarted ON queriesReceived.networkQueryId = executionsStarted.networkQueryId LEFT OUTER JOIN executionsCompleted ON queriesReceived.networkQueryId = executionsCompleted.networkQueryId LEFT OUTER JOIN resultsSent ON queriesReceived.networkQueryId = resultsSent.networkQueryId;